function burgerMenu(){const e=document.querySelector(".burger"),t=document.querySelector(".menu"),s=document.querySelector(".info"),c=document.querySelector(".main__body");e.addEventListener("click",(()=>{t.classList.contains("active")?(e.classList.remove("active-burger"),t.classList.remove("active"),s.classList.remove("active"),c.classList.remove("locked")):(e.classList.add("active-burger"),t.classList.add("active"),s.classList.add("active"),c.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>995&&(e.classList.remove("active-burger"),t.classList.remove("active"),s.classList.remove("active"),c.classList.remove("locked"))}))}function textChange(){const e=document.querySelector(".footer__left-text");window.addEventListener("resize",(()=>{window.innerWidth<740?e.textContent="Производство запасных частей \nдля грузового коммерческого\nтранспорта":e.textContent="Продажа запасных частей ГАЗ, ВАЗ, ПАЗ, УАЗ, КАМАЗ"}))}burgerMenu(),textChange();