function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),c=document.querySelector(".info"),t=document.querySelector(".body");e.addEventListener("click",(()=>{s.classList.contains("active")?(e.classList.remove("active-burger"),s.classList.remove("active"),c.classList.remove("active"),t.classList.remove("locked")):(e.classList.add("active-burger"),s.classList.add("active"),c.classList.add("active"),t.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>995&&(e.classList.remove("active-burger"),s.classList.remove("active"),c.classList.remove("active"),t.classList.remove("locked"))}))}burgerMenu();